mapper.insert:

    SELECT NVL(MAX(TO_NUMBER(PAY_ID)) , 0) + 1 PAY_ID FROM PAYMENT_DELIVERY

INSERT INTO PAYMENT_DELIVERY
(
    PAY_ID
   ,NAME
   ,POST_NUMBER
   ,ADDRESS1
   ,ADDRESS2
   ,PHONE
   ,PAY_TYPE
   ,MESSAGE
   ,USER_ID
   ,BUY_DATE
)
VALUES
(
    #{pay.payId}
   ,#{pay.name}
   ,#{pay.postNumber}
   ,#{pay.address1}
   ,#{pay.address2}
   ,#{pay.phone}
   ,#{pay.payType}
   ,#{pay.message}
   ,#{pay.userId}
   ,SYSDATE
)

mapper.select:
SELECT A.PNO ,A.PNAME ,A.PINFO ,A.PMAINIMG ,A.PSUBIMG ,B.PSIZE ,B.QTY ,B.PRICE FROM SHOPPING A ,(SELECT PNO ,PSIZE ,SUM(QTY) QTY ,SUM(QTY) * MAX(PRICE) PRICE FROM PAYMENT WHERE PAY_ID IN ( SELECT PAY_ID FROM PAYMENT_DELIVERY WHERE 1 = 1
AND USER_ID = #{userId}
AND BUY_DATE BETWEEN TO_DATE(#{startDate} , 'YYYY-MM-DD') AND TO_DATE(#{endDate} , 'YYYY-MM-DD')
) GROUP BY PNO , PSIZE) B WHERE A.PNO = B.PNO ORDER BY A.PNAME , B.PSIZE
